import { gql } from 'apollo-boost';

export const usersQuery = gql`
    query users() {
        users() {
            id
            name
            age
            movies {
                name
                id
            }
        }
    }
`;

export const directorsQuery = gql`
    query directorsQuery($name: String) {
        directors(name: $name) {
            id
            name
        }
    }
`;
